import React, { useState } from "react";
import axios from "axios";
export default function SearchEngine(props) {
  const [weather, setWeather] = useState({ ready: false });
  const [city, setCity] = useState("");

  function handleSubmit(event) {
    event.preventDefault();
  }
  function updateCity(event) {
    setCity(event.target.value);
  }
  function displayWeather(response) {
    setWeather({
      ready: true,
      temperature: response.data.main.temp,
      description: response.data.weather[0].description,
      wind: response.data.wind.speed,
      humidity: response.data.main.humidity,
      icon: `http://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`,
    });
  }
  let form = (
    <div className="container">
      <form onSubmit={handleSubmit}>
        <input type="search" placeholder="Enter a city" onChange={updateCity} />
        <input type="submit" value="Search" />
      </form>
    </div>
  );
  if (weather.ready) {
    return (
      <div className="SearchEngine">
        <div className="container">
          {form}
          <br />
          <div className="row">
            <div className="col-sm-6">
              <h4>Weather in {props.data.city}:</h4>
            </div>
            <div className="col-sm-6">
              <ul>
                <li>{Math.round(weather.temperature)}Â°C</li>
                <li>Description: {weather.description}</li>
                <li>{weather.humidity}%</li>
                <li>{weather.wind}km/h</li>
                <li>
                  <img src={weather.icon} alt={weather.description} />
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    let url = `https://api.openweathermap.org/data/2.5/weather?q=${props.defaultCity}&appid=57821c3b75b60c68ecd1a8d0dd1aa8d3&units=metric`;

    axios.get(url).then(displayWeather);
    return form;
  }
}
